func init() {
	err := json.Unmarshal([]byte(iso639Data), &allLanguages)
	if err != nil {
		panic(err)
	}

	// build indices
	for i := range allLanguages {
		v := &allLanguages[i]
		idxISO639_1[v.Code1] = v
		idxISO639_2[v.Code2] = v
		idxByName[strings.ToLower(v.Name)] = v
	}
}
